import React, { useEffect, useState, createContext } from "react";
import {
	FlatList,
	Keyboard,
	Text,
	TextInput,
	TouchableOpacity,
	View,
	SafeAreaView,
	StatusBar,
	StyleSheet,
	Image,
} from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createDrawerNavigator } from "@react-navigation/drawer";
import BladderHome from "../BladderOutcomeMeasures/BladderHome";
import { createStackNavigator } from "@react-navigation/stack";
import { Button } from "react-native-paper";
import { DefaultTheme, Provider as PaperProvider } from "react-native-paper";
// import Slideshow from "react-native-slideshow";
import Carousel, { Pagination } from "react-native-x2-carousel";
// import Carousel from "react-native-snap-carousel";

const Drawer = createDrawerNavigator();

const DATA = [
	{
		url: "./image7.png"
	},
	{
		url: "./image11.png"
	},
	{ url: "./image7.png" }
];
// const DATA = [{ text: "#1" }, { text: "#2" }, { text: "#3" }];

function HomeScreen({ navigation }) {
	const handlePress = () => <Bladder />;
	const renderItem = (data) => (
		<View key={data.url} style={styles.item}>
			<Image source={require('./image7.png')} />

			{/* <Text>{data.text}</Text> */}
		</View>
	);
	return (
		<SafeAreaView style={styles.container}>
			<Text style={styles.baseText} onPress={handlePress}>
				Welcome to Pelvic Health Outcomes {"\n"}
			</Text>
			<View
			// style={{ textAlign: "center" }}
			>
				<Text style={styles.bodyText}> Swipe right or</Text>
				<Button
					style={{ width: 150, marginLeft: "auto", marginRight: "auto" }}
					mode="contained"
					color="steelblue"
					onPress={() => navigation.openDrawer()}
				>
					Tap Here
				</Button>
				<Text style={styles.bodyText}>to view the categories.</Text>
			</View>

			<View style={styles.carousel}>
				<Carousel pagination={Pagination} renderItem={renderItem} data={DATA} />
			</View>
			{/* <Button
				color="orange"
				title="click me"
				onPress={() => alert("button tapped")}
			>
				Click Me
			</Button> */}
			<StatusBar style="auto" />
		</SafeAreaView>
	);
}

const theme = {
	...DefaultTheme,
	roundness: 2,
	colors: {
		...DefaultTheme.colors,
		primary: "steelblue",
		accent: "steelblue",
	},
};

export default function Apps(props) {
	// console.log("route.params", route.params);
	// console.log("props", props);
	// console.log("props.extraData", props.extraData);
	// let userId = props.extraData.fullName;

	let x = 1;
	const [user, setUser] = useState(props.extraData);

	return (
		<PaperProvider theme={theme}>
			<NavigationContainer independent={true}>
				<Drawer.Navigator initialRouteName="Home" headerBackTitle="null">
					<Drawer.Screen name="Home" component={HomeScreen} />
					<Drawer.Screen name="Urinary Outcomes" component={BladderHome} />
				</Drawer.Navigator>
			</NavigationContainer>
		</PaperProvider>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
		alignItems: "center",
		justifyContent: "center",

		fontSize: 40,
	},
	baseText: {
		marginTop: 200,
		fontFamily: "Cochin",
		fontSize: 30,
		textAlign: "center",
	},
	bodyText: {
		textAlign: "center",
		fontSize: 20,
		marginBottom: 10,
		marginTop: 10,
		fontFamily: "Arial Rounded MT Bold",
	},
	button: {
		alignItems: "center",
		backgroundColor: "#DDDDDD",
		padding: 10,
	},
	carousel: {
		flex: 1,
		backgroundColor: "#fff",
		alignItems: "center",
		justifyContent: "center",
	},
	item: {
		width: 200,
		height: 200,
		alignItems: "center",
		justifyContent: "center",
		backgroundColor: "#dbf3fa",
	},
});
